import React, {Component} from "react";

const {Provider, Consumer: AuthConsumer} = React.createContext({
    isAuthorized: false
});

class AuthProvider extends  Component{
    state = { isAuthorized: false}

    authorize = () => {
        this.setState({isAuthorized: true})
    }

    
}
